import React from 'react';
import { useNavigate } from 'react-router-dom';

const games = [
  {
    id: 'snake',
    title: 'Snake Game',
    description: 'Classic snake game with modern twists. Collect food, avoid obstacles, and unlock special powers!',
    image: '🐍',
    path: '/snake'
  },
  {
    id: 'fruit-ninja',
    title: 'Fruit Ninja',
    description: 'Slice fruits with precision while avoiding bombs. Test your reflexes and aim for the high score!',
    image: '🍎',
    path: '/fruit-ninja'
  }
];

const HomeScreen = () => {
  const navigate = useNavigate();

  return (
    <div style={{
      padding: '2rem',
      maxWidth: '1200px',
      margin: '0 auto'
    }}>
      <h1 style={{
        textAlign: 'center',
        fontSize: '2.5rem',
        marginBottom: '2rem',
        color: '#2c3e50'
      }}>
        Arcade Games
      </h1>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: '2rem',
        padding: '1rem'
      }}>
        {games.map(game => (
          <div
            key={game.id}
            onClick={() => navigate(game.path)}
            style={{
              backgroundColor: '#fff',
              borderRadius: '15px',
              padding: '1.5rem',
              cursor: 'pointer',
              transition: 'transform 0.2s, box-shadow 0.2s',
              boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
              ':hover': {
                transform: 'translateY(-5px)',
                boxShadow: '0 6px 12px rgba(0, 0, 0, 0.15)'
              }
            }}
          >
            <div style={{
              fontSize: '4rem',
              textAlign: 'center',
              marginBottom: '1rem'
            }}>
              {game.image}
            </div>
            <h2 style={{
              textAlign: 'center',
              fontSize: '1.5rem',
              marginBottom: '1rem',
              color: '#2c3e50'
            }}>
              {game.title}
            </h2>
            <p style={{
              textAlign: 'center',
              color: '#666',
              lineHeight: '1.5'
            }}>
              {game.description}
            </p>
            <button
              style={{
                display: 'block',
                width: '100%',
                padding: '0.8rem',
                marginTop: '1rem',
                backgroundColor: '#3498db',
                color: '#fff',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
                fontSize: '1rem',
                transition: 'background-color 0.2s',
                ':hover': {
                  backgroundColor: '#2980b9'
                }
              }}
            >
              Play Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomeScreen;
